sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

	//if the status is unavailable,show a message below the field.
	// if the status is available, clear the value of current user.
    if (newValue == "unavailable") {
		g_form.setMandatory('current_user');
        g_form.showFieldMsg('current_user', 'Please select the user that is currently using this book', 'error');
    } else if (newValue == "available") {
        g_form.setValue('current_user', '');
    }


}
